script:
frappe.ui.form.on('Stock Entry', {
	onload: function(frm) {
		// Stock Entry Type ni filtrlash (Faqat kerakli 3 ta)
		frm.set_query('stock_entry_type', function() {
			return {
				filters: {
					'name': ['in', ['Услуги по заказу', 'Расход по заказу', 'Перемещение']]
				}
			};
		});
	},

	refresh: function(frm) {
		// Har safar ochilganda UI rules ni qo'llash
		apply_ui_rules(frm);
	},

	stock_entry_type: function(frm) {
		// Type o'zgarganda UI rules ni qo'llash
		apply_ui_rules(frm);
	},

	custom_sales_order: function(frm) {
		// Sales Order o'zgarganda Sales Order Item ni tozalash
		frm.set_value('custom_sales_order_item', '');

		// Sales Order Item uchun query filtri
		if (frm.doc.custom_sales_order) {
			frm.set_query('custom_sales_order_item', function() {
				return {
					query: 'premierprint.utils.stock_entry.get_sales_order_items_query',
					filters: {
						sales_order: frm.doc.custom_sales_order
					}
				};
			});
		}
	},

	custom_sales_order_item: function(frm) {
		// Sales Order Item tanlanganda BOM materiallarini yuklash
		if (frm.doc.custom_sales_order_item) {
			frappe.call({
				method: 'premierprint.utils.stock_entry.get_bom_materials',
				args: {
					sales_order_item_id: frm.doc.custom_sales_order_item
				},
				callback: function(r) {
					if (r.message) {
						// Avvalgi itemlarni tozalash
						frm.clear_table('items');

						// Yangi materiallarni qo'shish
						r.message.forEach(function(item) {
							let row = frm.add_child('items');
							row.item_code = item.item_code;
							row.item_name = item.item_name;
							row.description = item.description;
							row.qty = item.qty;
							row.uom = item.uom;
							row.stock_uom = item.stock_uom;
							row.conversion_factor = item.conversion_factor;
							row.s_warehouse = item.s_warehouse;
							row.cost_center = item.cost_center;
						});

						frm.refresh_field('items');

						// Warehouse o'zgarganda Cost Center avtomatik qo'yish
						setup_warehouse_change_handler(frm);
					}
				}
			});
		}
	}
});

// Items jadvalida warehouse o'zgarganda
frappe.ui.form.on('Stock Entry Detail', {
	s_warehouse: function(frm, cdt, cdn) {
		auto_set_cost_center(frm, cdt, cdn, 's_warehouse');
	},

	t_warehouse: function(frm, cdt, cdn) {
		auto_set_cost_center(frm, cdt, cdn, 't_warehouse');
	}
});

function apply_ui_rules(frm) {
	const entry_type = frm.doc.stock_entry_type;

	if (entry_type === 'Услуги по заказу') {
		// A) Услуги по заказу - Narx ko'rinadi va o'zgartiriladi

		// Supplier maydoni - Ko'rinsin va majburiy
		frm.set_df_property('custom_supplier', 'hidden', 0);
		frm.set_df_property('custom_supplier', 'reqd', 1);

		// From/To Sub Company - Yashirilsin
		frm.set_df_property('custom_from_sub_company', 'hidden', 1);
		frm.set_df_property('custom_to_sub_company', 'hidden', 1);
		frm.set_df_property('custom_from_sub_company', 'reqd', 0);
		frm.set_df_property('custom_to_sub_company', 'reqd', 0);

		// Items jadvalida narx (basic_rate) - Ko'rinsin va o'zgartirilsin
		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'hidden', 0);
		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'read_only', 0);
		frm.fields_dict.items.grid.update_docfield_property('amount', 'hidden', 0);

	} else if (entry_type === 'Расход по заказу') {
		// B) Расход по заказу - Narx butunlay yashiriladi

		// Supplier - Yashirilsin
		frm.set_df_property('custom_supplier', 'hidden', 1);
		frm.set_df_property('custom_supplier', 'reqd', 0);

		// From/To Sub Company - Yashirilsin
		frm.set_df_property('custom_from_sub_company', 'hidden', 1);
		frm.set_df_property('custom_to_sub_company', 'hidden', 1);
		frm.set_df_property('custom_from_sub_company', 'reqd', 0);
		frm.set_df_property('custom_to_sub_company', 'reqd', 0);

		// Items jadvalida narx (basic_rate, amount) - Butunlay yashirilsin
		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'hidden', 1);
		frm.fields_dict.items.grid.update_docfield_property('amount', 'hidden', 1);

	} else if (entry_type === 'Перемещение') {
		// C) Перемещение - Narx ko'rinadi lekin o'zgartirib bo'lmaydi

		// Supplier - Yashirilsin
		frm.set_df_property('custom_supplier', 'hidden', 1);
		frm.set_df_property('custom_supplier', 'reqd', 0);

		// From/To Sub Company - Ko'rinsin va majburiy
		frm.set_df_property('custom_from_sub_company', 'hidden', 0);
		frm.set_df_property('custom_to_sub_company', 'hidden', 0);
		frm.set_df_property('custom_from_sub_company', 'reqd', 1);
		frm.set_df_property('custom_to_sub_company', 'reqd', 1);

		// Items jadvalida narx (basic_rate) - Ko'rinsin lekin o'zgarmasin
		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'hidden', 0);
		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'read_only', 1);
		frm.fields_dict.items.grid.update_docfield_property('amount', 'hidden', 0);

	} else {
		// D) Boshqa turlar uchun - Default holatga qaytarish
		frm.set_df_property('custom_supplier', 'hidden', 1);
		frm.set_df_property('custom_supplier', 'reqd', 0);
		frm.set_df_property('custom_from_sub_company', 'hidden', 1);
		frm.set_df_property('custom_to_sub_company', 'hidden', 1);
		frm.set_df_property('custom_from_sub_company', 'reqd', 0);
		frm.set_df_property('custom_to_sub_company', 'reqd', 0);

		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'hidden', 0);
		frm.fields_dict.items.grid.update_docfield_property('basic_rate', 'read_only', 0);
		frm.fields_dict.items.grid.update_docfield_property('amount', 'hidden', 0);
	}

	frm.refresh_field('items');
}

function setup_warehouse_change_handler(frm) {
	// Items jadvalida warehouse o'zgarganda Cost Center avtomatik qo'yish
	frm.fields_dict.items.grid.grid_rows.forEach(function(row) {
		if (row.doc) {
			auto_set_cost_center_from_warehouse(row.doc);
		}
	});
}

function auto_set_cost_center(frm, cdt, cdn, warehouse_field) {
	let row = locals[cdt][cdn];
	auto_set_cost_center_from_warehouse(row);
	frm.refresh_field('items');
}

function auto_set_cost_center_from_warehouse(row) {
	// S_warehouse yoki T_warehouse dan Cost Center aniqlash
	let warehouse = row.s_warehouse || row.t_warehouse;

	if (warehouse) {
		let warehouse_lower = warehouse.toLowerCase();

		// Ombor nomida "poli" so'zi bo'lsa -> 100
		if (warehouse_lower.includes('poli')) {
			row.cost_center = '100 - Poligrafiya Department';
		}
		// Ombor nomida "reklama" so'zi bo'lsa -> 200
		else if (warehouse_lower.includes('reklama')) {
			row.cost_center = '200 - Reklama Department';
		}
		// Ombor nomida "suvenir" so'zi bo'lsa -> 300
		else if (warehouse_lower.includes('suvenir')) {
			row.cost_center = '300 - Suvenir Department';
		}
		// Default
		else {
			row.cost_center = '100 - Poligrafiya Department';
		}
	}
}

frappe.ui.form.on('Stock Entry', {
    // 1. SETUP: Form ochilishi bilan filtrlarni o'rnatamiz
    setup: function(frm) {
        frm.set_query('stock_entry_type', function() {
            return {
                filters: [
                    ['Stock Entry Type', 'name', 'in', ['Расход по заказу', 'Услуги по заказу', 'Перемещение']]
                ]
            };
        });
    },

    // 2. REFRESH: Form yuklanganda logikani ishlatamiz
    refresh: function(frm) {
        frm.trigger('apply_ui_rules');
    },

    // 3. O'ZGARISH: Tip o'zgarganda logikani ishlatamiz
    stock_entry_type: function(frm) {
        frm.trigger('apply_ui_rules');
    },

    // 4. MANTIQ MARKAZI
    apply_ui_rules: function(frm) {
        let type = frm.doc.stock_entry_type;

        // Reset (Avval hammasini tozalaymiz/yashiramiz)
        // Bu juda muhim, aks holda eski holat qotib qoladi
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);
        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 0);
        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 0);
        frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 0);

        // A) "Услуги по заказу"
        if (type === 'Услуги по заказу') {
            // Supplier majburiy
            frm.set_df_property('custom_supplier', 'hidden', 0);
            frm.set_df_property('custom_supplier', 'reqd', 1);
        } 

        // B) "Расход по заказу"
        else if (type === 'Расход по заказу') {
            // Narxlar yashirinadi (Maxfiylik)
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 1);
            frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 1);
        } 

        // C) "Перемещение" (Yangi qo'shilgan)
        else if (type === 'Перемещение') {
            // Narx ko'rinsin, lekin o'zgartirib bo'lmasin
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 1);
        }

        // Jadvalni yangilash
        frm.refresh_field('items');
    }
});

frappe.ui.form.on('Stock Entry', {
    setup: function(frm) {
        // 1. Stock Entry Type filtri
        frm.set_query('stock_entry_type', function() {
            return {
                filters: [
                    ['Stock Entry Type', 'name', 'in', ['Расход по заказу', 'Услуги по заказу', 'Перемещение']]
                ]
            };
        });

        // 2. Sales Order Item Query (BOM uchun)
        frm.set_query("custom_sales_order_item", function() {
            let so_id = frm.doc.custom_sales_order;
            if (!so_id) return { filters: { "parent": "null" } };
            return {
                query: "premierprint.premierprint.utils.stock_entry.get_sales_order_items_query",
                filters: { "sales_order": so_id }
            };
        });

        // 3. Omborlarni Filtrlash (JADVAL ICHIDA)
        frm.set_query("s_warehouse", "items", function(doc, cdt, cdn) {
            if (frm.doc.stock_entry_type === 'Перемещение' && frm.doc.custom_from_sub_company) {
                return get_warehouse_filter(frm.doc.custom_from_sub_company);
            }
        });

        frm.set_query("t_warehouse", "items", function(doc, cdt, cdn) {
            if (frm.doc.stock_entry_type === 'Перемещение' && frm.doc.custom_to_sub_company) {
                return get_warehouse_filter(frm.doc.custom_to_sub_company);
            }
        });
    },

    refresh: function(frm) {
        frm.trigger('apply_ui_rules');
    },

    stock_entry_type: function(frm) {
        frm.trigger('apply_ui_rules');
    },

    // Sub Company o'zgarganda filtrlarni yangilash
    custom_from_sub_company: function(frm) { frm.refresh_field('items'); },
    custom_to_sub_company: function(frm) { frm.refresh_field('items'); },

    // Sales Order o'zgarganda
    custom_sales_order: function(frm) {
        frm.set_value("custom_sales_order_item", "");
        frm.clear_table("items");
        frm.refresh_field("items");
    },

    // Item tanlanganda (BOM yuklash)
    custom_sales_order_item: function(frm) {
        if (frm.doc.custom_sales_order_item) {
            frm.clear_table("items");
            frm.refresh_field("items");

            frappe.call({
                method: "premierprint.premierprint.utils.stock_entry.get_bom_materials",
                args: { sales_order_item_id: frm.doc.custom_sales_order_item },
                freeze: true,
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        r.message.forEach(row => {
                            let child = frm.add_child("items");
                            frappe.model.set_value(child.doctype, child.name, row);
                        });
                        frm.refresh_field("items");
                    }
                    frm.trigger('apply_ui_rules'); // UI qoidalarini qayta qo'llash
                }
            });
        }
    },

    // --- UI MANTIQ MARKAZI ---
    apply_ui_rules: function(frm) {
        let type = frm.doc.stock_entry_type;

        // RESET
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);
        frm.set_df_property('custom_from_sub_company', 'hidden', 1);
        frm.set_df_property('custom_to_sub_company', 'hidden', 1);
        frm.set_df_property('custom_from_sub_company', 'reqd', 0);
        frm.set_df_property('custom_to_sub_company', 'reqd', 0);
        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 0);
        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 1);
        frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 0);

        // LOGIKA
        if (type === 'Услуги по заказу') {
            frm.set_df_property('custom_supplier', 'hidden', 0);
            frm.set_df_property('custom_supplier', 'reqd', 1);
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 0);
        } 
        else if (type === 'Расход по заказу') {
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 1);
            frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 1);
        } 
        else if (type === 'Перемещение') {
            frm.set_df_property('custom_from_sub_company', 'hidden', 0);
            frm.set_df_property('custom_to_sub_company', 'hidden', 0);
            frm.set_df_property('custom_from_sub_company', 'reqd', 1);
            frm.set_df_property('custom_to_sub_company', 'reqd', 1);
        }

        frm.refresh_field('items');
    }
});

// YORDAMCHI FUNKSIYALAR
function get_warehouse_filter(sub_company) {
    let filter_txt = "";
    if (sub_company === "Полиграфия") filter_txt = "Poli";
    else if (sub_company === "Реклама") filter_txt = "Reklama";
    else if (sub_company === "Сувенир") filter_txt = "Suvenir";

    return { filters: [['Warehouse', 'name', 'like', '%' + filter_txt + '%']] };
}

frappe.ui.form.on('Stock Entry Detail', {
    s_warehouse: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.s_warehouse) set_cost_center(frm, row);
    },
    t_warehouse: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        if (row.t_warehouse) set_cost_center(frm, row);
    }
});

function set_cost_center(frm, row) {
    let warehouse = row.s_warehouse || row.t_warehouse;
    if (!warehouse) return;

    if (warehouse.includes("Poli")) {
        frappe.model.set_value(row.doctype, row.name, "cost_center", "100 - Poligrafiya Department");
    } else if (warehouse.includes("Reklama")) {
        frappe.model.set_value(row.doctype, row.name, "cost_center", "200 - Reklama Department");
    } else if (warehouse.includes("Suvenir")) {
        frappe.model.set_value(row.doctype, row.name, "cost_center", "300 - Suvenir Department");
    }
}

frappe.ui.form.on("Purchase Order", {
    before_save(frm) {
        (frm.doc.items || []).forEach(row => {
            if (!row.schedule_date) {
                row.schedule_date = frappe.datetime.get_today();
            }
        });
    },

    refresh(frm) {
        if (frm.fields_dict.items?.grid) {
            const grid = frm.fields_dict.items.grid;

            // faqat yashiramiz
            grid.update_docfield_property("schedule_date", "hidden", 1);
            grid.update_docfield_property("schedule_date", "reqd", 0);

            grid.refresh();
        }
    }
});

frappe.ui.form.on('Stock Entry', {
    setup: function(frm) {
        // 1. Stock Entry Type filtri
        frm.set_query('stock_entry_type', function() {
            return {
                filters: [
                    ['Stock Entry Type', 'name', 'in', ['Расход по заказу', 'Услуги по заказу', 'Перемещение']]
                ]
            };
        });

        // 2. Sales Order Item Query
        frm.set_query("custom_sales_order_item", function() {
            let so_id = frm.doc.custom_sales_order;
            if (!so_id) return { filters: { "parent": "null" } };
            return {
                query: "premierprint.premierprint.utils.stock_entry.get_sales_order_items_query",
                filters: { "sales_order": so_id }
            };
        });
    },

    refresh: function(frm) {
        // Jadval ichidagi filtrlarni "Jarrohlik" yo'li bilan o'rnatamiz
        // Bu usul standart ERPNext filtrlarini bosib o'tadi
        frm.fields_dict['items'].grid.get_field('t_warehouse').get_query = function(doc, cdt, cdn) {
            // DEBUG: Konsolda ishlaganini tekshirish uchun
            console.log("Target Filter Ishladi! Nishon:", doc.custom_to_sub_company);

            // Agar "Перемещение" bo'lsa va Nishon kompaniya tanlangan bo'lsa
            if (doc.stock_entry_type === 'Перемещение' && doc.custom_to_sub_company) {
                return {
                    filters: {
                        'company': doc.custom_to_sub_company, // <--- REKLAMA shu yerdan olinadi
                        'is_group': 0
                    }
                };
            }

            // Boshqa payt -> Standart Company
            return {
                filters: {
                    'company': doc.company,
                    'is_group': 0
                }
            };
        };

        // Source Warehouse uchun ham xuddi shunday
        frm.fields_dict['items'].grid.get_field('s_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    'company': doc.company, // Manba doim asosiy Company
                    'is_group': 0
                }
            };
        };

        frm.trigger('apply_ui_rules');
    },

    stock_entry_type: function(frm) {
        frm.trigger('apply_ui_rules');
    },

    // Kompaniya o'zgarganda
    company: function(frm) {
        frm.clear_table("items");
        frm.refresh_field('items');
    },

    // Nishon kompaniya o'zgarganda -> Jadvalni yangilash
    custom_to_sub_company: function(frm) { 
        // Qatorlarni tozalash shart emas, lekin Refresh kerak
        frm.refresh_field('items'); 
    },

    custom_sales_order: function(frm) {
        frm.set_value("custom_sales_order_item", "");
        frm.clear_table("items");
        frm.refresh_field("items");
    },

    // BOM YUKLASH
    custom_sales_order_item: function(frm) {
        if (frm.doc.custom_sales_order_item) {
            frm.clear_table("items");
            frm.refresh_field("items");

            frappe.call({
                method: "premierprint.premierprint.utils.stock_entry.get_bom_materials",
                args: { sales_order_item_id: frm.doc.custom_sales_order_item },
                freeze: true,
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        r.message.forEach(function(row) {
                            let child = frm.add_child("items");
                            frappe.model.set_value(child.doctype, child.name, row);
                        });
                        frm.refresh_field("items");
                        frm.trigger('apply_ui_rules');
                    } else {
                        frappe.msgprint("BOM topilmadi. Materiallarni qo'lda kiriting.");
                    }
                }
            });
        }
    },

    apply_ui_rules: function(frm) {
        let type = frm.doc.stock_entry_type;

        // RESET
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        frm.set_df_property('custom_from_sub_company', 'hidden', 1); // O'lik maydon
        frm.set_df_property('custom_to_sub_company', 'hidden', 1);
        frm.set_df_property('custom_to_sub_company', 'reqd', 0);

        frm.set_df_property('custom_sales_order', 'hidden', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 0);

        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 0);
        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 1);
        frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 0);

        // LOGIKA
        if (type === 'Услуги по заказу') {
            frm.set_df_property('custom_supplier', 'hidden', 0);
            frm.set_df_property('custom_supplier', 'reqd', 1);
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 0);
        } 
        else if (type === 'Расход по заказу') {
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 1);
            frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 1);
        } 
        else if (type === 'Перемещение') {
            frm.set_df_property('custom_sales_order', 'hidden', 1);
            frm.set_df_property('custom_sales_order_item', 'hidden', 1);

            // To Sub Company ochiladi
            frm.set_df_property('custom_to_sub_company', 'hidden', 0);
            frm.set_df_property('custom_to_sub_company', 'reqd', 1);
        }

        frm.refresh_field('items');
    }
});
