frappe.ui.form.on('Stock Entry', {
    setup: function(frm) {
        // 1. Stock Entry Type filtri
        frm.set_query('stock_entry_type', function() {
            return {
                filters: [
                    ['Stock Entry Type', 'name', 'in', ['Расход по заказу', 'Услуги по заказу', 'Перемещение']]
                ]
            };
        });

        // 2. Sales Order Item Query
        frm.set_query("custom_sales_order_item", function() {
            let so_id = frm.doc.custom_sales_order;
            if (!so_id) return { filters: { "parent": "null" } };
            return {
                query: "premierprint.utils.stock_entry.get_sales_order_items_query",
                filters: { "sales_order": so_id }
            };
        });
    },

    refresh: function(frm) {
        // Jadval ichidagi filtrlarni "Jarrohlik" yo'li bilan o'rnatamiz
        // Bu usul standart ERPNext filtrlarini bosib o'tadi
        frm.fields_dict['items'].grid.get_field('t_warehouse').get_query = function(doc, cdt, cdn) {
            // DEBUG: Konsolda ishlaganini tekshirish uchun
            console.log("Target Filter Ishladi! Nishon:", doc.custom_to_sub_company);

            // Agar "Перемещение" bo'lsa va Nishon kompaniya tanlangan bo'lsa
            if (doc.stock_entry_type === 'Перемещение' && doc.custom_to_sub_company) {
                return {
                    filters: {
                        'company': doc.custom_to_sub_company, // <--- REKLAMA shu yerdan olinadi
                        'is_group': 0
                    }
                };
            }

            // Boshqa payt -> Standart Company
            return {
                filters: {
                    'company': doc.company,
                    'is_group': 0
                }
            };
        };

        // Source Warehouse uchun ham xuddi shunday
        frm.fields_dict['items'].grid.get_field('s_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    'company': doc.company, // Manba doim asosiy Company
                    'is_group': 0
                }
            };
        };

        frm.trigger('apply_ui_rules');
    },

    stock_entry_type: function(frm) {
        frm.trigger('apply_ui_rules');
    },

    // Kompaniya o'zgarganda
    company: function(frm) {
        frm.clear_table("items");
        frm.refresh_field('items');
    },

    // Nishon kompaniya o'zgarganda -> Jadvalni yangilash
    custom_to_sub_company: function(frm) { 
        // Qatorlarni tozalash shart emas, lekin Refresh kerak
        frm.refresh_field('items'); 
    },

    custom_sales_order: function(frm) {
        frm.set_value("custom_sales_order_item", "");
        frm.clear_table("items");
        frm.refresh_field("items");
    },

    // BOM YUKLASH
    custom_sales_order_item: function(frm) {
        if (frm.doc.custom_sales_order_item) {
            frm.clear_table("items");
            frm.refresh_field("items");

            frappe.call({
                method: "premierprint.utils.stock_entry.get_bom_materials",
                args: { sales_order_item_id: frm.doc.custom_sales_order_item },
                freeze: true,
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        r.message.forEach(function(row) {
                            let child = frm.add_child("items");
                            frappe.model.set_value(child.doctype, child.name, row);
                        });
                        frm.refresh_field("items");
                        frm.trigger('apply_ui_rules');
                    } else {
                        frappe.msgprint("BOM topilmadi. Materiallarni qo'lda kiriting.");
                    }
                }
            });
        }
    },

    apply_ui_rules: function(frm) {
        let type = frm.doc.stock_entry_type;

        // RESET
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        frm.set_df_property('custom_from_sub_company', 'hidden', 1); // O'lik maydon
        frm.set_df_property('custom_to_sub_company', 'hidden', 1);
        frm.set_df_property('custom_to_sub_company', 'reqd', 0);

        frm.set_df_property('custom_sales_order', 'hidden', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 0);

        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 0);
        frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 1);
        frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 0);

        // LOGIKA
        if (type === 'Услуги по заказу') {
            frm.set_df_property('custom_supplier', 'hidden', 0);
            frm.set_df_property('custom_supplier', 'reqd', 1);
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'read_only', 0);
        } 
        else if (type === 'Расход по заказу') {
            frm.fields_dict['items'].grid.update_docfield_property('basic_rate', 'hidden', 1);
            frm.fields_dict['items'].grid.update_docfield_property('amount', 'hidden', 1);
        } 
        else if (type === 'Перемещение') {
            frm.set_df_property('custom_sales_order', 'hidden', 1);
            frm.set_df_property('custom_sales_order_item', 'hidden', 1);

            // To Sub Company ochiladi
            frm.set_df_property('custom_to_sub_company', 'hidden', 0);
            frm.set_df_property('custom_to_sub_company', 'reqd', 1);
        }

        frm.refresh_field('items');
    }
});

/**
 * Stock Entry Custom Logic for Premier Print
 * 
 * Scenarios:
 * 1. "Расход по заказу" - Material issue for Sales Order (prices hidden)
 * 2. "Услуги по заказу" - Service material issue with editable prices
 * 3. "Перемещение" - Inter-company transfers with smart warehouse filtering
 *    - s_warehouse: ONLY from selected company
 *    - t_warehouse: NO FILTER - all warehouses from all companies
 */

frappe.ui.form.on('Stock Entry', {
    onload: function(frm) {
        // Filter stock_entry_type to show only our custom types
        frm.set_query('stock_entry_type', function() {
            return {
                filters: {
                    name: ['in', ['Расход по заказу', 'Услуги по заказу', 'Перемещение']]
                }
            };
        });

        // Set custom query for Sales Order Item (show item_name)
        frm.set_query('custom_sales_order_item', function() {
            if (!frm.doc.custom_sales_order) {
                return { filters: { name: ['=', ''] } };
            }
            return {
                query: 'premierprint.utils.stock_entry.get_sales_order_items_query',
                filters: {
                    parent: frm.doc.custom_sales_order
                }
            };
        });
    },

    refresh: function(frm) {
        apply_scenario_logic(frm);
        setup_warehouse_filters(frm);

        // Add button to load BOM materials for "Расход по заказу"
        if (frm.doc.stock_entry_type === 'Расход по заказу' && 
            frm.doc.custom_sales_order_item && 
            frm.doc.docstatus === 0) {
            frm.add_custom_button(__('Load BOM Materials'), function() {
                load_bom_materials(frm);
            });
        }
    },

    stock_entry_type: function(frm) {
        apply_scenario_logic(frm);
        setup_warehouse_filters(frm);
    },

    custom_sales_order: function(frm) {
        if (!frm.doc.custom_sales_order) {
            frm.set_value('custom_sales_order_item', '');
        }
    },

    company: function(frm) {
        // Refresh warehouse filters when company changes
        setup_warehouse_filters(frm);
    }
});

// Child table logic
frappe.ui.form.on('Stock Entry Detail', {
    items_add: function(frm, cdt, cdn) {
        setup_warehouse_filters(frm);
    }
});

function apply_scenario_logic(frm) {
    const type = frm.doc.stock_entry_type;

    if (type === 'Расход по заказу') {
        // Scenario A: Sales Order expense
        frm.set_df_property('custom_sales_order', 'reqd', 1);
        frm.set_df_property('custom_sales_order', 'hidden', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 0);
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        // Hide price fields in items table
        toggle_price_fields(frm, true, false);

    } else if (type === 'Услуги по заказу') {
        // Scenario B: Service with supplier
        frm.set_df_property('custom_supplier', 'reqd', 1);
        frm.set_df_property('custom_supplier', 'hidden', 0);
        frm.set_df_property('custom_sales_order', 'hidden', 0);
        frm.set_df_property('custom_sales_order', 'reqd', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 0);

        // Show price fields (editable)
        toggle_price_fields(frm, false, true);

    } else if (type === 'Перемещение') {
        // Scenario C: Inter-company transfer with smart warehouse logic
        // Hide ALL custom fields - we only need standard company + warehouses
        frm.set_df_property('custom_sales_order', 'hidden', 1);
        frm.set_df_property('custom_sales_order', 'reqd', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        // Show price fields (read-only)
        toggle_price_fields(frm, false, false);

    } else {
        // Default: hide all custom fields
        frm.set_df_property('custom_sales_order', 'hidden', 1);
        frm.set_df_property('custom_sales_order', 'reqd', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        toggle_price_fields(frm, false, false);
    }

    frm.refresh_fields();
}

function toggle_price_fields(frm, hide_completely, editable) {
    const grid = frm.fields_dict.items?.grid;
    if (!grid) return;

    if (hide_completely) {
        // Scenario A: Hide prices completely
        grid.update_docfield_property('basic_rate', 'hidden', 1);
        grid.update_docfield_property('basic_rate', 'read_only', 1);
        grid.update_docfield_property('amount', 'hidden', 1);
        grid.update_docfield_property('basic_amount', 'hidden', 1);
        grid.update_docfield_property('valuation_rate', 'hidden', 1);
    } else {
        // Show prices
        grid.update_docfield_property('basic_rate', 'hidden', 0);
        grid.update_docfield_property('amount', 'hidden', 0);
        grid.update_docfield_property('basic_amount', 'hidden', 0);

        if (editable) {
            // Scenario B: Editable
            grid.update_docfield_property('basic_rate', 'read_only', 0);
        } else {
            // Scenario C: Read-only
            grid.update_docfield_property('basic_rate', 'read_only', 1);
        }
    }

    grid.refresh();
}

function setup_warehouse_filters(frm) {
    const type = frm.doc.stock_entry_type;

    // Header level filters
    if (type === 'Перемещение') {
        // Source warehouse: only from selected company
        frm.set_query('from_warehouse', function() {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        });

        // Target warehouse: ALL warehouses (no company filter!)
        frm.set_query('to_warehouse', function() {
            return {
                filters: {
                    is_group: 0
                }
            };
        });
    }

    // Child table (items) warehouse filters
    const grid = frm.fields_dict.items?.grid;
    if (!grid) return;

    if (type === 'Перемещение') {
        // s_warehouse: only from selected company
        grid.get_field('s_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        };

        // t_warehouse: ALL warehouses from ALL companies (NO FILTER!)
        grid.get_field('t_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    is_group: 0
                }
            };
        };
    } else {
        // For non-transfer types, filter both by company
        grid.get_field('s_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        };

        grid.get_field('t_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        };
    }
}

function load_bom_materials(frm) {
    if (!frm.doc.custom_sales_order_item) {
        frappe.msgprint(__('Please select Sales Order Item first'));
        return;
    }

    frappe.call({
        method: 'premierprint.utils.stock_entry.get_bom_materials',
        args: {
            sales_order_item: frm.doc.custom_sales_order_item
        },
        callback: function(r) {
            if (r.message) {
                // Clear existing items
                frm.clear_table('items');

                // Add BOM materials
                r.message.forEach(item => {
                    let row = frm.add_child('items');
                    row.item_code = item.item_code;
                    row.item_name = item.item_name;
                    row.qty = item.qty;
                    row.uom = item.uom;
                    row.stock_uom = item.stock_uom;
                    row.conversion_factor = item.conversion_factor;
                    row.description = item.description;

                    // Set source warehouse from form default
                    if (frm.doc.from_warehouse) {
                        row.s_warehouse = frm.doc.from_warehouse;
                    }
                });

                frm.refresh_field('items');
                frappe.show_alert({
                    message: __('BOM materials loaded successfully'),
                    indicator: 'green'
                });
            }
        }
    });
}

frappe.ui.form.on("Purchase Order", {
    before_save(frm) {
        (frm.doc.items || []).forEach(row => {
            if (!row.schedule_date) {
                row.schedule_date = frappe.datetime.get_today();
            }
        });
    },

    refresh(frm) {
        if (frm.fields_dict.items?.grid) {
            const grid = frm.fields_dict.items.grid;

            // faqat yashiramiz
            grid.update_docfield_property("schedule_date", "hidden", 1);
            grid.update_docfield_property("schedule_date", "reqd", 0);

            grid.refresh();
        }
    }
});

/**
 * Stock Entry Custom Logic for Premier Print
 * 
 * SCENARIOS:
 * 1. "Расход по заказу" - Material issue for Sales Order (prices hidden)
 * 2. "Услуги по заказу" - Service material issue with editable prices  
 * 3. "Перемещение" - Inter-company transfers with SMART warehouse filtering
 *
 * WAREHOUSE LOGIC for "Перемещение":
 * - s_warehouse: ONLY from doc.company (filtered)
 * - t_warehouse: NO FILTER - all warehouses from all companies!
 */

frappe.ui.form.on('Stock Entry', {
    onload: function(frm) {
        // Filter stock_entry_type to show only our custom types
        frm.set_query('stock_entry_type', function() {
            return {
                filters: {
                    name: ['in', ['Расход по заказу', 'Услуги по заказу', 'Перемещение']]
                }
            };
        });

        // Set custom query for Sales Order Item
        frm.set_query('custom_sales_order_item', function() {
            if (!frm.doc.custom_sales_order) {
                return { filters: { name: ['=', ''] } };
            }
            return {
                query: 'premierprint.utils.stock_entry.get_sales_order_items_query',
                filters: { parent: frm.doc.custom_sales_order }
            };
        });
    },

    refresh: function(frm) {
        apply_ui_rules(frm);
        setup_warehouse_filters(frm);

        // Add BOM button for "Расход по заказу"
        if (frm.doc.stock_entry_type === 'Расход по заказу' && 
            frm.doc.custom_sales_order_item && 
            frm.doc.docstatus === 0) {
            frm.add_custom_button(__('Load BOM Materials'), function() {
                load_bom_materials(frm);
            });
        }
    },

    stock_entry_type: function(frm) {
        apply_ui_rules(frm);
        setup_warehouse_filters(frm);
    },

    company: function(frm) {
        setup_warehouse_filters(frm);
    },

    custom_sales_order: function(frm) {
        if (!frm.doc.custom_sales_order) {
            frm.set_value('custom_sales_order_item', '');
        }
    }
});

// Items table events
frappe.ui.form.on('Stock Entry Detail', {
    items_add: function(frm, cdt, cdn) {
        setup_warehouse_filters(frm);
    }
});

/**
 * Apply UI rules based on stock_entry_type
 */
function apply_ui_rules(frm) {
    const type = frm.doc.stock_entry_type;

    // ============ ALWAYS HIDE DEPRECATED FIELDS ============
    // These fields are no longer used - hiding them regardless of type
    if (frm.fields_dict.custom_from_sub_company) {
        frm.set_df_property('custom_from_sub_company', 'hidden', 1);
        frm.set_df_property('custom_from_sub_company', 'reqd', 0);
    }
    if (frm.fields_dict.custom_to_sub_company) {
        frm.set_df_property('custom_to_sub_company', 'hidden', 1);
        frm.set_df_property('custom_to_sub_company', 'reqd', 0);
    }

    // ============ SCENARIO-SPECIFIC LOGIC ============
    if (type === 'Расход по заказу') {
        // Scenario A: Sales Order expense
        frm.set_df_property('custom_sales_order', 'hidden', 0);
        frm.set_df_property('custom_sales_order', 'reqd', 1);
        frm.set_df_property('custom_sales_order_item', 'hidden', 0);
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        // Hide price fields completely
        toggle_price_fields(frm, true, false);

    } else if (type === 'Услуги по заказу') {
        // Scenario B: Service with supplier
        frm.set_df_property('custom_sales_order', 'hidden', 0);
        frm.set_df_property('custom_sales_order', 'reqd', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 0);
        frm.set_df_property('custom_supplier', 'hidden', 0);
        frm.set_df_property('custom_supplier', 'reqd', 1);

        // Show price fields (editable)
        toggle_price_fields(frm, false, true);

    } else if (type === 'Перемещение') {
        // Scenario C: Inter-company transfer
        // HIDE all custom fields - only use standard company + warehouses
        frm.set_df_property('custom_sales_order', 'hidden', 1);
        frm.set_df_property('custom_sales_order', 'reqd', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        // Show price fields (read-only)
        toggle_price_fields(frm, false, false);

    } else {
        // Default: hide all custom fields
        frm.set_df_property('custom_sales_order', 'hidden', 1);
        frm.set_df_property('custom_sales_order', 'reqd', 0);
        frm.set_df_property('custom_sales_order_item', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'hidden', 1);
        frm.set_df_property('custom_supplier', 'reqd', 0);

        toggle_price_fields(frm, false, false);
    }

    frm.refresh_fields();
}

/**
 * Toggle price fields visibility and editability
 */
function toggle_price_fields(frm, hide_completely, editable) {
    const grid = frm.fields_dict.items?.grid;
    if (!grid) return;

    if (hide_completely) {
        grid.update_docfield_property('basic_rate', 'hidden', 1);
        grid.update_docfield_property('basic_rate', 'read_only', 1);
        grid.update_docfield_property('amount', 'hidden', 1);
        grid.update_docfield_property('basic_amount', 'hidden', 1);
        grid.update_docfield_property('valuation_rate', 'hidden', 1);
    } else {
        grid.update_docfield_property('basic_rate', 'hidden', 0);
        grid.update_docfield_property('amount', 'hidden', 0);
        grid.update_docfield_property('basic_amount', 'hidden', 0);

        if (editable) {
            grid.update_docfield_property('basic_rate', 'read_only', 0);
        } else {
            grid.update_docfield_property('basic_rate', 'read_only', 1);
        }
    }

    grid.refresh();
}

/**
 * Setup warehouse filters based on stock_entry_type
 * 
 * For "Перемещение":
 * - s_warehouse: ONLY from doc.company
 * - t_warehouse: NO FILTER (all companies!)
 */
function setup_warehouse_filters(frm) {
    const type = frm.doc.stock_entry_type;
    const grid = frm.fields_dict.items?.grid;

    if (!grid) return;

    if (type === 'Перемещение') {
        // ========== ПЕРЕМЕЩЕНИЕ FILTERS ==========

        // Source warehouse: ONLY from selected company
        grid.get_field('s_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        };

        // Target warehouse: NO FILTER! All warehouses from all companies
        grid.get_field('t_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    is_group: 0
                }
            };
        };

        // Also set header-level warehouse filters
        frm.set_query('from_warehouse', function() {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        });

        frm.set_query('to_warehouse', function() {
            return {
                filters: {
                    is_group: 0
                }
            };
        });

    } else {
        // ========== OTHER TYPES: Filter both by company ==========
        grid.get_field('s_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        };

        grid.get_field('t_warehouse').get_query = function(doc, cdt, cdn) {
            return {
                filters: {
                    company: frm.doc.company,
                    is_group: 0
                }
            };
        };
    }
}

/**
 * Load BOM materials into items table
 */
function load_bom_materials(frm) {
    if (!frm.doc.custom_sales_order_item) {
        frappe.msgprint(__('Please select Sales Order Item first'));
        return;
    }

    frappe.call({
        method: 'premierprint.utils.stock_entry.get_bom_materials',
        args: { sales_order_item: frm.doc.custom_sales_order_item },
        freeze: true,
        freeze_message: __('Loading BOM materials...'),
        callback: function(r) {
            if (r.message && r.message.length > 0) {
                frm.clear_table('items');

                r.message.forEach(item => {
                    let row = frm.add_child('items');
                    row.item_code = item.item_code;
                    row.item_name = item.item_name;
                    row.qty = item.qty;
                    row.uom = item.uom;
                    row.stock_uom = item.stock_uom;
                    row.conversion_factor = item.conversion_factor;
                    row.description = item.description;

                    if (frm.doc.from_warehouse) {
                        row.s_warehouse = frm.doc.from_warehouse;
                    }
                });

                frm.refresh_field('items');
                frappe.show_alert({
                    message: __('BOM materials loaded: {0} items', [r.message.length]),
                    indicator: 'green'
                });
            } else {
                frappe.msgprint(__('BOM topilmadi. Materiallarni qo\'lda kiriting.'));
            }
        }
    });
}
